import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import Button from '@mui/material/Button';
import config from '../../config/config';
import Image from './log.svg';
import Image2 from './bgGif.gif';

const Welcome = () => {
  const navigate = useNavigate();
  const [WelcomeData, setWelcomeData] = useState([]);


  useEffect(() => {
    axios.get(`${config.apiUrl}/Pagesdata`)
      .then((response) => {
        console.log('Data received:', response.data);
        setWelcomeData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  const handleButtonClick = () => {
    navigate('/dashboard');
  };

  return (
    <div style={{ display: 'flex', width: '100%', height: '100vh' }}>
      <div style={{ width: '50%', display: 'flex', justifyContent: 'center', alignItems: 'center', overflow: 'hidden' }}>
      {WelcomeData.map((item, index) => (
        // <img style={{ width: '100%' }} src={Image2} alt="" />
        <img alt="" src={item.welcome.Ima.welcome_bg_img} />

      ))}
        <div style={{ position: 'absolute' }}>
        </div>
      </div>
      <div style={{ width: '50%', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <div className="signin-signup">
          <form action="#" className="sign-in-form">
            <h2 className="title">Welcome to Workiy CRM!</h2>
            <h4>Weâ€™re excited to help you streamline your business operations and enhance your customer relationships. Get started now and explore the features tailored to your needs.

</h4>
<h4>Thank you for choosing Workiy-CRM your partner in success!</h4>
            <Button variant="contained" color="primary" onClick={handleButtonClick}>
              Let's get started
            </Button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default Welcome;
